int main() {
    int numbers[] = {3, 41, 12, 9, 74, 15};
    int n = sizeof(numbers) / sizeof(numbers[0]);
    int largest = numbers[0];
    for (int i = 1; i < n; i++) {
        if (numbers[i] > largest) {
            largest = numbers[i];
        }
    }
    printf("%d\n", largest);
    return 0;
}
